import React, { Component } from "react";

export class Toggle extends Component {
  state = {
    signIn: false,
    signUp: true,
  };

  toggle = () => {
    this.setState({
      signIn: !this.state.signIn,
      signUp: !this.state.signUp,
    });
  };

  autre() {
    const { render } = this.props;
    console.log("RENDER", this);
    return (
      <div>
        {render({
          signIn: this.state.signIn,
          signUp: this.state.signUp,
          toggle: this.toggle,
        })}
      </div>
    );
  }
}

export default Toggle;
