import React from "react";
import LogIn from "./LogIn";
import SignUp from "./SignUp";
import Toggle from "./Toggle";

const SigninForm = () => {
  const modalStateChanger = () => {
    this.setState({
      signIn: !this.state.signIn,
      signUp: !this.state.signUp,
    });
  };
  const handleModals = (e) => {
    if (e.target.id === "login") {
      modalStateChanger();
    } else if (e.target.id === "register") {
      modalStateChanger();
    }
  };

  return (
    <section className="homeSection">
      <div className="bckgrd">
        <img src="../img/icon.png" alt="" />
      </div>
      <div className="pageTitle">
        <h1>Le r√©seau social d'entreprise</h1>
      </div>
      <div className="log-list">
        <ul>
          <li id="login" onClick={handleModals}>
            Se connecter
          </li>
          <li id="register" onClick={handleModals}>
            S'inscrire
          </li>
        </ul>
      </div>
      <div className="formBlock">
        <Toggle
          autre={({ signIn, signUp, modalStateChanger }) => (
            <div>
              {signIn && <LogIn />}
              {signUp && <SignUp />}
              <button onClick={modalStateChanger}> SHOW /HIDE</button>
            </div>
          )}
        />
      </div>
    </section>
  );
};

export default SigninForm;
