import React, { Component } from "react";

export class Toggle extends Component {
  state = {
    toggleOn: false,
    toggleOff: true,
  };

  toggle = () => {
    this.setState({
      toggleOn: !this.state.toggleOn,
      toggleOff: !this.state.toggleOff,
    });
  };
  render() {
    const { render } = this.props;
    return (
      <div>
        {render({
          toggleOn: this.state.toggleOn,
          toggleOff: this.state.toggleOff,
          toggle: this.toggle,
        })}
      </div>
    );
  }
}

export default Toggle;
